mapper.select:
SELECT default_consumption_tax_rate
FROM company_details
WHERE company_id = #{companyId}

SELECT COUNT(*) > 0
FROM users
WHERE id = #{userId}
  AND company_id = #{companyId}

mapper.update:
UPDATE users
SET
	name		= #{userName},
	authority	= #{userAuthority}
WHERE id = #{userId}

mapper.select:
SELECT authority
FROM users
WHERE id = #{userId}
