mapper.select:
SELECT
DATE_FORMAT(earnings_date,"%Y") AS "groupdate",
DATE_FORMAT(earnings_date,"%Y/%m") AS "groupdate",
(CASE WHEN TRUNCATE(SUM(income_money),-3) > 18000000 THEN (0.4 * TRUNCATE(SUM(income_money),-3) - 2796000) WHEN TRUNCATE(SUM(income_money),-3) > 9000000 THEN (0.33 * TRUNCATE(SUM(income_money),-3) - 1536000) WHEN TRUNCATE(SUM(income_money),-3) > 6950000 THEN (0.23 * TRUNCATE(SUM(income_money),-3) - 636000) WHEN TRUNCATE(SUM(income_money),-3) > 3300000 THEN (0.2 * TRUNCATE(SUM(income_money),-3) - 427500) WHEN TRUNCATE(SUM(income_money),-3) > 1950000 THEN (0.1 * TRUNCATE(SUM(income_money),-3) - 97500) WHEN SUM(income_money) > 0 THEN 0.05 * TRUNCATE(SUM(income_money),-3) ELSE 0 END ) AS "money" FROM( SELECT earnings.earnings_date AS "income_date", earnings.money AS "income_money" FROM earnings INNER JOIN suppliers ON earnings.suppliers_id = suppliers.id AND earnings.company_id = suppliers.company_id
earnings.company_id = #{companyAccountId}
AND suppliers.name LIKE #{companyName}
AND earnings.suppliers_id = #{companyId}
AND suppliers.is_displayed = TRUE
UNION ALL ( SELECT expenses.expenses_date AS "income_date", (0 - expenses.money * expenses_item.rate) AS "income_money" FROM expenses INNER JOIN expenses_item ON expenses.expenses_item_id = expenses_item.id AND expenses.company_id = expenses_item.company_id
expenses.company_id = #{companyAccountId}
AND suppliers.is_displayed = TRUE
) ) income_sum
income_date >= #{earningsDateStart}
AND #{earningsDateFinish} >= income_date
GROUP BY groupdate
